    private TalonFX fuelGrabber;
    private TalonFX hopperMotor1;
    private TalonFX hopperMotor2;

    private DigitalInput hopperExtendSwitch;
    private DigitalInput hopperRetractSwitch;
    public XboxController m_joystick = new XboxController(0);

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DECLARATIONS
    TalonFX m_fx = new TalonFX(15, "rio");
    /* Be able to switch which control request to use based on a button press */
    /* Start at position 0, use slot 0 */

    final PositionVoltage m_positionVoltage = new PositionVoltage(0).withSlot(0);
    /* Start at position 0, use slot 1 */
    final PositionTorqueCurrentFOC m_positionTorque = new PositionTorqueCurrentFOC(0).withSlot(1);
    /* Keep a brake request so we can disable the motor */
    final NeutralOut m_brake = new NeutralOut();

    /**
    *
    */
    public Hopper() {
        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTORS
        fuelGrabber = new TalonFX(14, "rio");

        hopperMotor1 = new TalonFX(15, "rio");

        hopperMotor2 = new TalonFX(16, "rio");

        hopperExtendSwitch = new DigitalInput(0);
        addChild("hopperExtendSwitch", hopperExtendSwitch);

        hopperRetractSwitch = new DigitalInput(1);
        addChild("hopperRetractSwitch", hopperRetractSwitch);

        // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTORS
        TalonFXConfiguration configs = new TalonFXConfiguration();
        configs.Slot0.kP = 2.4; // An error of 1 rotation results in 2.4 V output
        configs.Slot0.kI = 0; // No output for integrated error
        configs.Slot0.kD = 0.1; // A velocity of 1 rps results in 0.1 V output
        // Peak output of 8 V
        configs.Voltage.withPeakForwardVoltage(Volts.of(8))
                .withPeakReverseVoltage(Volts.of(-8));
        CurrentLimitsConfigs fxCurrent = configs.CurrentLimits;
        fxCurrent.StatorCurrentLimit = 5;

        configs.Slot1.kP = 60; // An error of 1 rotation results in 60 A output
        configs.Slot1.kI = 0; // No output for integrated error
        configs.Slot1.kD = 6; // A velocity of 1 rps results in 6 A output
        // Peak output of 120 A
        configs.TorqueCurrent.withPeakForwardTorqueCurrent(Amps.of(120))
                .withPeakReverseTorqueCurrent(Amps.of(-120));
        /* Retry config apply up to 5 times, report if failure */
        StatusCode status = StatusCode.StatusCodeNotInitialized;
        for (int i = 0; i < 5; ++i) {
            status = m_fx.getConfigurator().apply(configs);
            if (status.isOK())
                break;
        }
        if (!status.isOK()) {
            System.out.println("Could not apply configs, error code: " + status.toString());
        }

        /* Make sure we start at 0 */
        m_fx.setPosition(0);
    }

    @Override
    public void periodic() {
        // This method will be called once per scheduler run
        SmartDashboard.putBoolean("Hopper Extended", isHopperExtended());
        SmartDashboard.putBoolean("Hopper Retracted", isHopperRetracted());
        SmartDashboard.putNumber("position", m_fx.getPosition().getValueAsDouble());
        double desiredRotations = m_joystick.getLeftY() * 10; // Go for plus/minus 10 rotations
        if (Math.abs(desiredRotations) <= 0.1) { // Joystick deadzone
            desiredRotations = 0;
        }
        desiredRotations = 2;

        if (m_joystick.getLeftBumperButton()) {
            /* Use position voltage */
            m_fx.setControl(m_positionVoltage.withPosition(desiredRotations));
            SmartDashboard.putBoolean("buttonPressed", true);
        } else if (m_joystick.getRightBumperButton()) {
            /* Use position torque */
            m_fx.setControl(m_positionTorque.withPosition(desiredRotations));
        } else {
            SmartDashboard.putBoolean("buttonPressed", false);
            /* Disable the motor instead */
            m_fx.setControl(m_brake);
        }

        if (isHopperRetracted()) {
            m_fx.setPosition(0);
        }

    }

    @Override
    public void simulationPeriodic() {
        // This method will be called once per scheduler run when in simulation
    }

